function postIt(){window.note=document.getElementById("postar"),note.style.display="block"}function erasePost(){note.style.display="none"}function postContent(t){note.innerText=t}function dinamicPost(t){return["0","joao","2","3"][t]}function onPost(){for(i=0;i<3;i++)setTimeout((()=>postContent(dinamicPost(i))),3e3)}function clickPost(){switch(click+=1,click){case 4:click=0,postContent(dinamicPost(click));break;default:postContent(dinamicPost(click));break}}window.click=0;